import React, { useEffect } from "react";
import MovieListing from "../MovieListing/MovieListing";
import axios from 'axios';
import { APIkey } from '../../common/apis/MovieApiKey';
import { useDispatch } from "react-redux";
import { addMovies } from "../../Features/movies/movieSlice";

const Home = () => {
  useEffect(() => {
   
    const fetchMovies = async () => {
      try {
        console.log("Using API Key:", `"${APIkey}"`);  // Logging the API key to verify its format
        const response = await axios.get(`https://www.omdbapi.com/?apiKey=${APIkey.trim()}&s=${movieText}&type=movie`);
        console.log("The response from API: ", response.data);
      } catch (err) {
        console.error("Error fetching movies:", err);
        if (err.response) {
          console.error("Response data:", err.response.data);
          console.error("Response status:", err.response.status);
          console.error("Response headers:", err.response.headers);
        } else if (err.request) {
          console.error("Request data:", err.request);
        } else {
          console.error("Error message:", err.message);
        }
      }
      dispatch(addMovies(response.data))
    };

    fetchMovies();
  }, []);

  return (
    <div>
      <div className="banner-img"></div>
      <MovieListing />
    </div>
  );
};

export default Home;
